css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("songza.com") {
body {
background: #002245 !important;
color: #FFFFFF!important;
}
#query,#submit{
border:none!important;
}
.box{
background:#001133!important;
border:none!important;
}
#watch,#buy,#discography{
background:#000015!important;
color:white!important;
}
.tabs-container,.tabs-nav li{
background:#001133!important;
color:white!important;
}
a.song:hover{
color:#0033AA!important;
}
.tabs-nav a{
color:white!important;
}
#results-more{
background:#000015!important;
color:white!important;
}
#ad-header{
border:none!important;
display:none!important;
}
#footer-nav a{
color:#0066AA!important;
}
#play-marker{
display:none!important;
}
}
