css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/dashboard"),
url-prefix("http://www.tumblr.com/blog"),
url("http://www.tumblr.com/likes"),
url-prefix("http://www.tumblr.com/show/") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/show/") {

  ol.notes > li.note {
    padding: 0 !important;
  }
  
  ol.notes > li.note > a.avatar_frame > span.border {
    border: none !important;
  }

  ol.notes > li.note > a.avatar_frame:before,
  li.notification > a.avatar_frame:before{
    background: none !important;
    border: none !important;
    box-shadow: none !important;
  }
  
  ol.notes > li.note > a.avatar_frame:after,
  li.notification > a.avatar_frame:after {
    background: none !important;
    border: none !important;
  }
  
  .report {
    top: 7px !important;
  }

  li.notification > div.notification_inner {
    min-height: 25px !important;
    padding: 0 !important;
  }
  
  li.notification > a.avatar_frame,
  li.notification > div.notification_right,
  li.notification > div.notification_right > a.preview_frame {
    top: 0 !important;
  }
}
