css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") 

{

.notification_sentence
{
font-weight: normal !important;
padding: 2px 0px 4px 23px !important;
letter-spacing: 1px !important;
}

.notification
{
font-size:10px !important;
line-height: 11px !important;
}

.notification .notification_inner
{
position: relative;
padding: 3px 10px 0px 15px !important;
}

.notification .notification_right img, .notification .avatar_frame img, .notes .note .avatar_frame img, .ui_avatar .frame img
{
    float: right;
margin-top: -5px !important;
    width: 20px !important;
    height: 20px !important;
    border-radius: 2px;
}

.notification .avatar_frame img, .notes .note .avatar_frame img, .ui_avatar .frame img
{
margin-top: -7px !important;
}
    
.notification .notification_right .preview_frame
{
margin-top: -2px !important;
    width: 10px !important;
    height: 10px !important;
    border-radius: 2px;
}

.notification_target
{
font-style: italic !important;
letter-spacing: 0px !important;  
font-size:9px !important;
}
    
}
