@namespace url(http://www.w3.org/1999/xhtml);

/*
Name: wiki / baike - easy to copy
ver 0.3 (2014.1.13)
Author: tumuyan
Description: http://userstyles.org/styles/80165
*/



@-moz-document domain("wikipedia.org"), domain("zwbk.org"), regexp(".*wiki.*"), regexp(".*baike.*view.*") {
span.editsection,#siteNotice,#mw-articlefeedback,#footer,
a[href^="javascript:locationEdit"],
span[class="text_edit editable-title"],
.content_h2 > h2 > a#bjbd,
span.mw-editsection,
 /* wikipedia.org # citation */
#mw-content-text > h2 + ul
{display:none !important;}
}
