css:
@-moz-document url("http://www.youtube.com/"), url("https://www.youtube.com/"), url-prefix("http://www.youtube.com/feed/"), url-prefix("https://www.youtube.com/feed/") {
  #yt-masthead-container #yt-masthead {
    margin-left:0px!important;
    margin-right:0px!important;
  }
  #logo-container {
    margin-left:0px!important;
    margin-right:0px!important;
    padding-left:54px!important;
  }
  #yt-masthead-user {
    margin-left:0px!important;
    margin-right:0px!important;
    padding-right:45px!important;
  }
  #yt-masthead-content {
    margin-left:auto!important;
    margin-right:auto!important;
    padding-left:1.1%!important;
  }
  #page-container #page {
    margin-left:auto!important;
    margin-right:auto!important;
  }

}

@-moz-document url-prefix("http://www.youtube.com/results") {
  #yt-masthead-container #yt-masthead {
    margin-left:0px!important;
    margin-right:0px!important;
  }
  #logo-container {
    margin-left:0px!important;
    margin-right:0px!important;
    padding-left:54px!important;
  }
  #yt-masthead-user {
    margin-left:0px!important;
    margin-right:0px!important;
    padding-right:45px!important;
  }
  #yt-masthead-content {
    margin-left:auto!important;
    margin-right:auto!important;
    padding-left:1.1%!important;
  }
  #page.search-base {
    margin-left:0px!important;
  }
  #guide {
    padding-left:45px!important;
  }
  #page.search-base #content {
    width:642px;
    margin-left:auto!important;
    margin-right:auto!important;
  }
}
