css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
    
    /*
     * 18-Dec-2012
     * Nombre: YouTubetter
     * Versión: 1.1
     * Por: Eduardo Martínez
     * Twitter: @Ardoye
     */
    
    * { font-family: "Segoe UI Symbol" !important;}
    
    body
    {
        background: #ddd !important;
        margin-top: 3.75em !important;
    }
    
    /* Columna principal */
    /*#content*/
    .branded-page-v2-col-container.clearfix , .primary-col
    {
        box-shadow: 0 1.2em 2em #999 !important;
    }
        
    /* Cabecera */
    #yt-masthead-container
    {
        position: fixed !important;
        top: 0 !important;
        left: 0 !important;
        z-index: 50 !important;
        width: 100% !important;
       
        /*box-shadow: 0 -1.4em 5em #610B0B !important;*/
        box-shadow: 0 0em 0.4em #999 !important;
        border-color: #999 !important;        
    }
    
    /* INPUT Cabecera */
    #yt-masthead-container input
    {
        color: #610B0B !important;
    }
    
    /* Despegable listas usuario */
    #masthead-expanded-container
    {
        box-shadow: inset 0 0 1em black !important;
        background: #222 !important;
    }
    
    /* Comentarios de vídeo */
    #watch7-content
    {
        box-shadow: 0.1em 0.1em 0.2em #999 !important;
    }
    
    /* AD */
    #header
    {
        display: none !important;
    }

}
