css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("agqr.jp") {
    BODY {
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: white !important; }
    INPUT[type=button] { color: white !important; }
    TEXTAREA           { color: white !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }
    /* プレイヤーへのリンク */
    div#agright div#agrightupper div#agplus a#agplus_play {
        /*background-image: none !important;  /* */
        position: fixed !important;
        right:  10px !important;
        top:    10px !important;
    }
    /* 番組表へのリンク */
    div#agright div#agrightupper div#agplus a#agplus_ttable {
        position: fixed !important;
        right:  10px !important;
        top:    90px !important;
    }
    /* ゲスト出演情報 */
    DIV#guestinfo { color: #181818 !important; }
    #agbg { background-image: none !important; }
    #agcontent { background-color: transparent !important; }
    #boxframe_m { background-image: none !important; }
    #timetable #boxframe_m TD { background-color: transparent !important; }
}
