css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mysql.com") {
    BODY {
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: black !important; }
    INPUT[type=button] { color: black !important; }
    TEXTAREA           { color: black !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }

    DIV#current_pane, DIV.dijitTabPaneWrapper, TR.alt,
    DIV.alert02, DIV.dijitTabChecked {
        background-color: transparent !important;
    }
    DIV.section DIV.titlepage H3 {
        background-color: transparent !important;
        color: white !important;
    }
    DIV.section DIV.titlepage H4 {
        background-color: transparent !important;
        color: white !important;
    }
    CODE.literal, STRONG {
        background-color: transparent !important;
        color: #a0a0ff !important;
    }
    PRE.programlisting {
        background-color: transparent !important;
        color: white !important;
    }
    #menu {
        background-image: none !important;
    }
    #mysql_menu {
        background-image: none !important;
    }
    #tab_last {
        background-image: none !important;
        background-color: transparent !important;
    }
    #tab_last UL {
        background-image: none !important;
        background-color: transparent !important;
    }
    #tab_last LI {
        background-color: gray !important;
        border-top-left-radius:  10px !important;
        border-top-right-radius: 10px !important;
        margin: 2px !important;
    }
    #tab_last LI.first {
        background-image: none !important;
    }
    #tab_last LI.first A {
        padding: 5px 20px !important;
    }
    #tab_last LI.second A {
        background-image: none !important;
        padding: 5px 20px !important;
    }
    #tab_last LI.current A {
        background-image: none !important;
        background-color: transparent !important;
        padding: 5px 20px !important;
    }
    #page > DIV {
        background-color: transparent !important;
    }
    #footer {
        background-color: transparent !important;
    }
}
