css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(tohoho-web.com) {
    BODY {
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: white !important; }
    INPUT[type=button] { color: white !important; }
    TEXTAREA           { color: white !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }

    TH, TD { color: white !important; }
    TH     { background-color: #3F3F3F !important; }

    INPUT, SELECT, TEXTAREA {
        background-color: black !important;
        color: white !important;
    }
    /** for / */
    DIV.adv DIV {
        background-color: transparent !important;
        color: gray !important;
    }
    /** for /www.htm */
    TD.info {
        background-color: transparent !important;
        color: white !important;
    }
    TH.google {
        background-color: transparent !important;
        color: white !important;
    }
    TH.google IMG {
        background-color: white !important;
    }
    TD.left {
        background-color: transparent !important;
        color: gray !important;
    }
    TD.adv2 {
        background-color: transparent !important;
        color: gray !important;
    }
    /** for /html/ */
    TABLE.p {
        background-color: transparent !important;
    }
    TABLE.p TD {
        color: white !important;
    }
    PRE.c, PRE.c2, DIV.c2 {
        background-color: transparent !important;
    }
}
