css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("esuteru.com") {
    BODY {
        background-image: none !important;
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: white !important; }
    INPUT[type=button] { color: white !important; }
    TEXTAREA           { color: white !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }

    #entry > .body BLOCKQUOTE { background-color: transparent !important; }
    /* コメント. 名前 */
    #entry .foot #comment INPUT[type="text"] { background-color: transparent !important; }
    /* コメント. 文章 */
    #entry .foot #comment TEXTAREA { background-color: transparent !important; }
    /* 幅 */
    DIV#content   { width: 760px !important; }
    #header .wrap { width: 760px !important; }

    DIV#content,
    DIV#main_col
    { background-color: transparent !important; }
    DIV#main_col
    { float: none !important; }
    ASIDE#right_col,
    DIV#headline
    { display: none !important; }
}
