@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filedropper.com") {

/* Hides "Header"
#wrap > div:first-child { display: none !important; } */

/* Hides Sidebar And Ads */
#sidebar, div[class*="ad"] { display: none !important; }

/* Hides "SignUp" */
a[href*="createaccount"] { display: none !important; }

/* Hides "Free Forums" */
a[href*="forumotion.com/"] { display: none !important; }

/* Hides "Safari Bug" */
#safari_rememberDiv { display: none !important; }

/* Hides "Footer" */
.bottommenu, .footersignup, .innerbottommenu { display: none !important; }

}
