css:
@-moz-document url-prefix("http://www.etsy.com/") {
/* Featured Seller */
.featured-seller .image, .recent-blog-posts .image {
  opacity: .2; -webkit-transition: opacity, height .5s; height:50px; }
.featured-seller .image:hover, .recent-blog-posts .image:hover { opacity: 1; height:150px; }
.ticker li.listing { opacity: .2; -webkit-transition: opacity .5s; }
.ticker li.listing:hover { opacity: 1; }

/* Promotions */
#primary .promotions .browse-card-inner { opacity: .1; height: 50px; -webkit-transition: opacity, height .5s; }
#primary .promotions .browse-card-inner:hover { opacity: 1; height:150px; }

/* Search Results */
.listings .listing-card, .section-block.s2 div.scroller-view ul.listings { height: auto; }
.listing-thumb img {opacity: .2; -webkit-transition: opacity .5s; }
.listing-thumb img:hover { opacity: 1; }
.listing-card > a { height:50px; -webkit-transition: height .5s; overflow:hidden; }
.listing-card > a:hover { height:135px; overflow:hidden; }
.section.section-block.s2 { height:125px; -webkit-transition: height .5s; }
.section.section-block.s2:hover { height:205px; }

/* Product Pages */
#image-main { height:50px; overflow:hidden; }
#image-main:hover { height:100%; }
#image-main img { opacity: .1; -webkit-transition: opacity .5s; }
#image-main img:hover { opacity: 1; }

/* Product Pages - Top Seller Info */
#seller-wrapper img { -webkit-transition: opacity .5s; opacity:.1; }
#seller-wrapper img:hover { opacity: 1; }

/* Product Pages - Sidebar Seller Info */
#related-listings-container img { opacity:.1; }
#related-listings-container img:hover { opacity:1; }

}
