css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://bt.ktxp.com/") {

/*首页宽屏*/
.container-style{
width:auto;
margin:0px 148px 0px 165px !important;
}

.icon{
margin-right:150px !important;
}

#recommend{
/*margin-left:165px !important;*/
display:none !important;
}

.main{
width:auto !important;
}

/*返回顶部*/
#toTop{
margin-right:8% !important;
}

/*隐藏*/
.kkkmh,
ul.right,
#bdshare,
#bottom{
display:none !important;
}

@-moz-document url-prefix("http://bt.ktxp.com/html/") {

/* 下载页宽屏拉伸 */
.main {width: auto !important;}
.layout2 {padding: 0 0px 0 0 !important;}
.infos ul {width: 80% !important;}

/*标题*/
h1{
margin-left:18% !important;
}

/*种子详情*/
.infos.container-style{
margin-left:15% !important;
}

/*资源详情，文件信息，网友评论按钮*/
.navigation{
margin-left:36% !important;
}

/*举报按钮*/
.report.cmbg{
margin-right:25% !important;
}

/*资源详情*/
.intro.container-style{
margin-left:15% !important;
}

/*下载列表*/
#down-box{
margin:2% 0 2% 17% !important;
}

/* 下载页下载框位置调整 */
.base .down {height: 24px !important;}
#down-box>.left {display:none !important;}
#down-box>.right {float: left !important; width: 100% !important;}

/* 下载页下载按钮位置重排 */
.right>a {float: none !important; position: absolute !important;}
.right>a:nth-of-type(1) {margin: 0px 0px 6px 438px !important;}
.right>a:nth-of-type(2) {margin: 0px 0px 6px 0px !important;}
.right>a:nth-of-type(3) {margin: 0px 0px 6px 584px !important;}
.right>a:nth-of-type(4) {margin: 0px 0px 6px 146px !important;}
.right>a:nth-of-type(5) {margin: 0px 0px 6px 730px !important;}
.right>a:nth-of-type(6) {margin: 0px 0px 6px 292px !important;}
.right>a:nth-of-type(7) {display:none !important;}
.right>a:nth-of-type(8) {display:none !important;}

/*资源详情标题*/
.space-top{
color:blue;
margin-left:15% !important;
}

/*文件列表*/
.filelist{
margin:0 4% 0 5%!important;
}
}

@-moz-document url-prefix("http://bt.ktxp.com/comment"){
/*标题*/
h1{
margin-left:23% !important;
}

/*资源详情，文件信息，网友评论按钮*/
.navigation{
margin-left:42% !important;
}

/*举报按钮*/
.report.cmbg{
margin-right:7% !important;
}

/*评论*/
#comments{
margin:0 -10% 0 8% !important;
}
}
}
