css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("unfuddle.com") {

/* reduce the height of the dark bar at the top from 40 to 25 pixels */
    .navbar-search { margin-top: 0 !important; }
    .navbar li { line-height: normal !important; }
    #account_header .navbar-inner { min-height: 25px !important; }
    #account_header.navbar .nav > li > a { padding-top: 5px !important; padding-bottom: 4px !important; }
    #account_header.navbar ul.nav .account_name a { padding-top: 3px !important; padding-bottom: 0 !important; }
    #account_header.navbar-inverse .navbar-search .search-query { padding-top: 0 !important; padding-bottom: 0 !important; }
    .navbar input[type="text"] { height: 19px !important; }
    #ajax_static { margin-top: 3px !important; }
    #ajax_static, #ajax_working { height: 19px !important; background-size: 19px !important; }

/* move up the menu bar and reduce it's height from 35 to 20 pixels */
    #header.header-fixed-top { top: 25px !important; }
    #header .navbar-inner { min-height: 20px !important; }
    #header.navbar .nav > li > a { padding-top: 3px !important; padding-bottom: 3px !important; }
    
/* move the rest of the content up to fill the space */
    #main { margin-top: -30px !important; }
}
