css:
@-moz-document domain('bash.im')
{
    body
    {
        color: #525252 !important;
        background: #aAaEb6 !important;
    }
    a:link, div.actions span.b-share__text
    {
        color: #64625A !important;
        border-bottom: solid 1px !important;
    }
    a:visited
    {
        color: #5A567F !important;
        border-bottom: none !important;    
    }
    a:hover
    {
        color: #303252 !important;
    }
    div#xsite-nav
    {
        background: #3C4048 !important;
    }
    div#xsite-nav a.current, div#xsite-nav a:hover, .submenu div.options a.current, .submenu div.options span.current
    {
        background: #8B8B8D !important;
    }
    div.actions a:hover
    {
        color: #6A6A6D !important;
        background: #9D9DA4 !important;
    }
    input[type="text"], textarea, div#search button
    {
        color: #333333 !important;
        background: #CCCCCC !important;
    }
    .submenu div.options
    {
        margin-left: 100px !important;
    }
    div.text
    {
        background-color: #AEB2BB !important;
        color: #525252 !important;
        border: 1px dashed #6A6F6F !important;
        border-radius: 8px !important;
        padding: 20px !important;
    }
    div .date
    {
        color: #626262 !important;
        position: relative !important;
        left: 0px !important;
        margin-left: 10px !important;
    }
    div#body
    {
        width:70% !important;
    }
    div.quote div.actions .down, div.quote div.actions .up, div.actions .rating, div.quote div.actions .share, div.quote div.actions .old
    {
        visibility:hidden;
    }
    div.quote div.actions .id
    {
        border-bottom: none !important;
    }
}
