@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

#home_button {
    padding-right: 17px !important;
}

#home_button a {
    height: 0 !important;
    width: 0 !important;
    top: -2px !important;
    padding-left: 40px !important;
    padding-top: 40px !important;
    background: url('http://imageshack.com/a/img199/1320/j5bz.png') !important;
}

#home_button .tab_notice {
    left: 45px !important;
    right: auto !important;
}

}
