css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://events.ccc.de/congress/2012/Fahrplan/"),url-prefix("https://events.ccc.de/congress/2012/Fahrplan/")
{
  body
  {
    color: black !important;
    background-color: white !important;
  }

  #menu span.normal
  {
    color:black !important;
  }

  :link, *:visited
  {
    color: #020284 !important;
  }

  #schedule th, th, #schedule td
  {
    color: black !important;
    background-color: white !important;
  }

  #schedule td
  {
    border: 2px solid black;
  }

  #schedule table
  {
    border-collapse: collapse !important;
  }

  #infobox
  {
    background-color: white !important;
  }

  #navigation span
  {
    background-color: white !important;
    color: black !important;
  }
}
