css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nasa.gov") {
  #ullitags,
  div.white_article_wrap_detail div.default_style_wrap,
  div.white_article_wrap_detail div.prejs_body_adjust_detail {
    font-family: "Lucida Grande","Lucida Sans Unicode","Eras Medium ITC","DejaVu Sans",Helvetica,Verdana,sans-serif !important;
    font-size: medium !important;
    line-height: 1.4em !important;
  }
  #ullitags *,
  div.default_style_wrap div.prejs_body_adjust_detail *,
  div.white_article_wrap_detail div.prejs_body_adjust_detail * {
    font-size:  smaller !important;
  }
}
