css:
/* 
target: facebook
function: sticky left column with collapsed subitems
by: pfeiffer stylez customizing
http://userstyles.org/users/170753
*/

@-moz-document domain("facebook.com") {

#facebook .home #leftCol {
position: fixed !important;
}

#facebook .uiSideNav .homeSideNav:not(#pinnedNav) .sideNavItem {
margin-left: 0px !important;
padding-right: 0px !important;
}
#facebook .uiSideNav .homeSideNav:not(#pinnedNav) .item, .uiSideNav .homeSideNav:not(#pinnedNav) .subitem {
margin: 0px !important;
}
#facebook .uiSideNav .homeSideNav:not(#pinnedNav) .buttonWrap {
float: right !important;
margin-right: -10px;
}
#facebook .uiSideNav .homeSideNav:not(#pinnedNav) .uiSelectorMenuWrapper {
left: auto !important;
right: 0px !important;
}

#facebook .homeSideNav:not(#pinnedNav) .sideNavItem {
height: 0px !important;
overflow: hidden !important;
transition: height 0.2s linear 0.2s !important;
-moz-transition: height 0.2s linear 0.2s !important;
-webkit-transition: height 0.2s linear 0.2s !important;
-o-transition: height 0.2s linear 0.2s !important;
}

#facebook .homeSideNav:not(#pinnedNav):hover .sideNavItem {
height: 24px !important;
}
#facebook .homeSideNav:not(#pinnedNav) .sideNavItem:hover {
overflow: visible !important;
}

#facebook .homeSideNav:not(#pinnedNav) h4:after  {
padding-left: 9px !important;
content:"  â–¼";
transition: all 0.2s linear 0.2s !important;
-moz-transition: all 0.2s linear 0.2s !important;
-webkit-transition: all 0.2s linear 0.2s !important;
-o-transition: all 0.2s linear 0.2s !important;
}
#facebook .homeSideNav:not(#pinnedNav):hover h4:after {
color: transparent !important;
text-shadow: none !important;
}

h4.navHeader .rfloat {display: none !important;}

#facebook #pagelet_navigation .homeSideNav h4,
#facebook #pagelet_navigation .homeSideNav h4 *,
#facebook #pagelet_navigation .moreSectionsLink {
text-align: left !important;
}

#facebook #pagelet_navigation .homeSideNav h4:not(.hidden_elem) {
word-wrap: normal !important;
white-space: nowrap !important;
display: inline-block !important;
}

#facebook #pagelet_navigation .homeSideNav h4 a,
#facebook #pagelet_navigation .homeSideNav h4 .clearfix,
#facebook #pagelet_navigation .homeSideNav h4 .lfloat {
display: inline-block !important;
line-height: 12px !important;
vertical-align: bottom !important;
}
#facebook #pagelet_navigation .homeSideNav h4,
#facebook #pagelet_navigation .moreSectionsLink {
width: auto !important;
padding: 0px 0px 0px 8px !important;
margin: 0px !important;
}
#facebook #pagelet_navigation .homeSideNav h4 a,
#facebook #pagelet_navigation .homeSideNav h4 .clearfix
#facebook #pagelet_navigation .homeSideNav h4 .lfloat {
width: auto !important;
padding: 0px !important;
margin: 0px !important;
}
#facebook #pagelet_navigation .homeSideNav h4 a {
margin-right: -8px !important;
}

#facebook #pagelet_navigation .bookmarksNavSeeAll {
display: none !important;
}

#pinnedNav h4.accessible_elem {
width: 100% !important; height: auto !important;
position: relative !important;
}
#navItem_app_4748854339 ._6a {right: 0px !important;}
#navItem_app_4748854339 .buttonWrap {margin-right: 0px !important;}

}
