css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nzb.su") {

    body {
        background-color: #222 !important;
        color: #aaa !important;
    }
        
    tr:nth-child(odd) td {
      background-color: #333 !important;
    }
    
    tr:nth-child(even) td{
      background-color: #444 !important;
    }
    
    #browsetable tbody tr th {
        color: #fff !important;
        background-color: #333 !important;
        background-image: none !important;
    }
    
    a {
        color: #ddd !important;
    }
    
    a:hover {
        color: #fff !important;
    }
    
    .dropdown-menu {
        background-color: #444 !important;
    }
    
    li.active a,
    li a:hover {
        background-color: #444 !important;
    }
    
    .accordion-heading {
        background-color: #444 !important;
    }
    
    .tab-content {
        background-color: #333 !important;
    }
    
    .well {
        background-color: #444 !important;
    }
    
    .btn {
        background-color: #333 !important;
        background-image: none !important;
    }
    
    .alert-success {
        background-color: #00B424 !important;
    }

}
