/* about.com Disable Signature Chop-Off 0.1 */

/* This short script disables the signature size restriction
   of 500 pixels vertical. */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.about.com") {

td.pfMsgBody div.pfMsgSig
	{
		max-height:none !important;
	}

}
