css:
@-moz-document regexp("http?://\\www\\.baidu\\.com\\/.+") {
* { -webkit-transition:background-color 50ms linear!important; }

.s_help,#foot,.ec_bdtg { display:none!important; }

.c-default { padding: 10px!important;}
.c-default .t { padding: 10px 0!important;}

#content_left .result-op,#content_left .result {  background:#fff!important; border:#fff solid 1px!important; padding:5px 5px!important; margin:0 0 10px 0!important; }
#content_left .result-op:hover,#content_left .result:hover { background:#f9f9f9!important; border:#eee solid 1px!important; border-radius:3px!important; box-shadow:5px 5px 0px #eee!important; }
#content_right .result-op { background:#fff!important; }
}

@-moz-document url-prefix("http://www.baidu.com/") {
div#bottom_container { display:none!important;}
}
