css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("^https?://(www|beta)\\.nerdz\\.eu.*") {
    *, button, input
    {
        font-family: 'Tahoma';
        font-weight: 400;
    }
    .small
    {
        font-size: 9px !important;
    }
    textarea
    {
        font-size: 13px;
    }
    #rightmenu li, #leftmenu li
    {
        margin-bottom: -7px;
    }
    #left_col .title[style^="width"], #right_col .title[style^="width"]
    {
        margin-bottom: -17px;
    }
    .nerdz_from
    {
        margin-bottom: 0px !important;
    }
    .news
    {
        background: #000 !important;
        color: #FFF !important;
        border: 1px solid #FFBB33 !important;
    }
    .news hr
    {
        display: none;
    }
    .news .small
    {
        margin-top: 5px;
    }
    .news a[style="color:#000"]
    {
        color: #2370B6 !important;
    }
    .frmcomment textarea, #stdfrm textarea
    {
        margin-bottom: -4px;
    }
    input[value="Nerdz!"]
    {
        margin-left: 2px;
        margin-right: 2px;
    }
    #stdfrm input
    {
        margin-right: 4px;
    }
    .nerdz_message
    {
        margin-bottom: -5px !important;
    }
    .nerdz_message[style^="margin"]
    {
        margin-bottom: 5px !important;
    }
    .quote
    {
        /* Fix quote boxes being 'stopped' by
           post timing. */
        margin-top: 15px !important;
        border-radius: 2px;
        padding: 2px 2px 2px 2px;
    }
    .nerdz_comments .quote
    {
        margin-top: 0px !important;

    }
    .nerdz_from .nerdz_date
    {
        margin-right: 5px !important;
    }
    /* nessuno, Y U NO USE MARGIN CORRECTLY? */
    #footersearch
    {
        margin-top: 1px !important;
        text-align: left;
        margin-left: 32px;
    }
    /* Fix searchbar's width */
    #footersearch input
    {
        width: 200px !important;
    }
    #footer_main
    {
        height: 23px !important; /* -1px to fix searchbar pos */
    }
    #bug_bar, #notifycounter
    {
        height: 18px !important;
    }
    #notify_list
    {
        margin-bottom: -1px;
    }
    #pmessage, .error
    {
        padding-top: 10px;
        margin-left: 140px !important;
        margin-bottom: -10px;
    }
    .qu_user
    {
        padding: 1px 1px 1px 1px;
    }
    hr
    {
        color: #fff;
        background-color: #fff;
        height: 1px;
        border: 0;
    }
    .spoiler
    {
        border-radius: 3px;
        padding: 2px 2px 2px 2px;
    }
    /* Fix padding issues in the header (@nessuno :*) */
    #gotopm, nav div
    {
        margin-top: 3px !important;
        text-align: right !important;
    }
    #gotopm
    {
        margin-left: -40px;
        margin-top: -16px !important;
    }
    nav
    {
        text-align: right;
    }
    /* Fix the position of the top bar */
    header
    {
        position: fixed;
        top: 0;
        box-shadow: 2px 2px 5px #000;
        /* and again, thanks alchimist */
        z-index: 1;
        /*background-color: rgba(175,175,175,0.6) !important;*/
    }
    /* Implemented Alchimist's fix. Should fix every
       bad content position issues -- reapplied again */
    /*body
    {
        padding-top: 55px;
    }*/
    #center_col
    {
        margin-top: 55px;
        background-color: rgba(0,0,0,0.25);
        box-shadow: 0px 2px 5px #414141;
        padding-left: 4px !important;
        padding-top: 4px;
    }
    /* Fix comment's quote padding 
     ** REMOVED since nessuno fixed it.
      But still kept commented, we can expected
      everything from nessuno... xD
    .qu_main
    {
        clear: none !important;
    }
    /* The <br> hack is epic. You're
       amazing, Robertof. Oh, I know. 
    .nerdz_comments br
    {
        clear: both;
    }*/
    body
    {
        margin-top: 55px !important;
    }
    /* fix for gravatar stuff */
    img[style^="float:left; padding-right:2px"]
    {
        padding-top: 5px !important;
    }
    /* fix fonts and colors in code/pre tags */
    /* pre { background: #000; color: #fff; overflow: auto; } */
    pre span {
        font-family: "monospace", "Courier New" !important;
        font-size: 12px;
    }
    /* better messaging starts now */
    .getconv { cursor: pointer; }
    #conversation .nerdz_message {
        margin-bottom: 5px !important ;
    }
    /* fix hr */
    .nerdz_comments hr {
        color: #000 !important; background-color: #000 !important;
    }
    div[id^="commentlist"] div[style^="width:33"] { margin-top: -3px; }
    /* Potential fix for auto image scaling
       removed because we also need an userscript
       patching the imgBig function
    img[height="130"]
    {
        max-height: 130px;
        max-width: 130px;
        width: auto !important; height: auto !important;
    }
    span[onclick^="N.imgBig"] span {
        min-width: 90px !important;
        width: auto !important;
    }*/
    #mainFaggotry p, #haxor div, #haxor p, #haxor span { font-family: 'Consolas', 'monospace', 'Courier New', serif !important; }
    .singlecomment {
        /* Hack to allow the anchors to work properly */
        padding-top: 55px;
        margin-top: -55px;
    }
    /* fix for shitty browser (aka: webkit based ones) */
    @media screen and (-webkit-min-device-pixel-ratio:0)
    {
        article {
            margin-top: 0px !important;
        }
    }
}
