css:
@-moz-document url-prefix("http://narida.pytalhost.com") {

body {
  background-color: #222 !important;
  color: #ddd !important;
}

#sheet {
 margin-top: 20px;
}

#skills {
 margin-top: 30px;
}

.detail, .ability {
  width: 260px !important;
  border-left: 4px solid #444 !important;
  border-radius: 4px;
  padding: 4px 8px;
}

.evo div {
  border: 1px solid #bbb !important;
  background-color: #888 !important;
  border-radius: 4px !important;
}

.evo .hover {
  border: 1px solid #eee !important;
  background-color: #666 !important;
}

.evo .selected {
  background-color: #008DC9 !important;
  border: 1px solid #9DE0FC !important;
  color: #9DE0FC !important;
}

.shields {
  color: #9DE0FC !important;
}

em.attribute {
  color: #7CCC74 !important;
}

em.evobonus {
  color: #008DC9 !important;
}

em.gear {
  color: #DE6A62 !important;
}

.armor {
  color: #FFB429 !important;
}

.barrier {
  color: #A680E8 !important;
}

a {
 color: #8093E8 !important;
}

.tip {
  border: 2px solid #666 !important;
  border-radius: 4px !important;
  background-color: #ddd !important;
  color: #222 !important;
  padding: 8px !important;
}

select {
  background-color: #ddd !important;
  min-width: 200px;
}

.detailname {
 color: #eee !important;
 padding-bottom: 2px !important;
}

.skilltree .name {
  text-align: left !important;
}

.evo4, .evo6, .evo8 {
  top: 70px !important;
}
}
