css:
@-moz-document domain("bitbucket.org") {
body {
  font-family: Helvetica, arial, freesans, clean, sans-serif;
  font-size: 13px;
}

.aui-header .aui-header-logo, .aui-header .aui-nav {
  font-size: 14px;
  font-weight: bold;
}
.aui-button, a.aui-button {
  font-family: Helvetica, arial, freesans, clean, sans-serif;
  font-size: 13px;
  padding-top: 4px;
  font-weight: bold;
}

.repo-summary .repo-metadata {
  font-size: 13px;
  margin-top:4px;
}
.repo-summary .repo-metadata .icon {
  margin-top: 0px;
}

#repo-overview #repo-stats .stats .value {
  font-size: 24px;
  font-weight: bold;
}
.repo-summary > h1 {
  font-size: 20px;
  font-weight: bold;
}
.clone-url input {
  height: 28px;
}

.bb-toolbar a span.icon {
  margin-top: 0px;
}
.mac #source-tree {
  display: none;
}
}
