css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.shopbot.com.au") {

body, header #search-form #search
{
background:#000000 !important;
}
#cookie-banner, .results-actions,
.p-search .filters-block .header
{
background:#FF6F0C !important;
}
.p-search .filters-block, footer, #popular,
header #menu ul, 
#as_ul, .as_bar, .as_corner, .autosuggest a
{
background:#111111 !important;
}
#wrap
{
background:#222222 !important;
}
.module.clean .header, .module ul, #popular .group
{
border-color:#FF6F0C !important;
}
.p-search .filters-block, .results-actions,
header #menu ul
{
border:1px #FF6F0C solid !important;
}
.p-search .filters-block .header,
.small-title, .left h1, .right h1, .full h1,
.list .result
{
border-bottom:1px #FF6F0C solid !important;
}
#popular .item
{
border-bottom:1px #FF6F0C dotted !important;
}
.left #categories-list .universe .categories
{
border-left:1px #FF6F0C solid !important;
}
.p-search .filters-block .header
{
text-shadow:none !important;
}
header #menu ul
{
box-shadow:none !important;
}
.result .price span, .price
{
color:#00FF00 !important;
}
.result .discount del, .compare-price,
.compare-price .display-title span, .total,
a:hover
{
color:#FF6F0C !important;
}
#cookie-banner, #breadcrumb li a span, .result .name,
.p-search .filters-block .header, .result .description,
header #search-form #search, .results-actions, .label,
.small-title, .left h1, .right h1, .full h1, .promo, a,
.group-title, .facet-category, .facet-shop, .facet-brand,
.module.clean .header, .title, .product-details
{
color:#FFFFFF !important;
}
}
