css:
@-moz-document url-prefix("http://cinemassacre.com/") {
body{
background:#121212;
color:#dfdfdf;
line-height:17px;
font-family: "Arial", "Helvetica";
}

.content{
background:#191919;
-webkit-box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
padding:0 10px 0 10px;
}

#featured{
background:#1d1d1d;
border:1px solid #292929;
-webkit-box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
}

.archiveitem:hover img, #featuredImg:hover a img {
border: 1px solid #2f2f2f;
-webkit-box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
}

#featured li div.featureditem:hover img {
border: 1px solid #2f2f2f;
-webkit-box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
}

.listitem:hover img, #featuredImg:hover img{
border: 1px solid #2f2f2f;
-webkit-box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
box-shadow:  0px 0px 4px 0px rgba(0, 0, 0, 0.65);
}

#rightCol{
width:321px;
background:#151515;
padding:5px;
}
#leftCol{
width:615px;
background:#151515;
padding:15px;
}
}
