css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cvsbook.red-bean.com")
{
    /* CVS Book causes a scrollbar across bottom of FFx.
     * It is apparently caused by the fixed-width font
     * used in the GPL licensing notice at the very
     * bottom of the site. */

    pre.example
      {
          font-family : "Meslo LG S", "Inconsolata",  Menlo, monospaced !important;
            font-size : 9pt !important;
      }

    p
      {
          font-family : "Cambria", serif  !important;
      }

    ul *, ol *, dd
      {
          font-family : "Book Antigua", serif  !important;
      }

    pre, p
      {
           max-width : 588px              !important;
      }

    ul *, ol *
      {
         margin-left :-1.0ex              !important;
           max-width : 540px              !important;
      }
    ul * a, ol * a
      {
          padding-left : 3px              !important;
            font-size  : 10pt             !important;
      }

    li
      {
       margin-bottom : 2.3ex              !important;
      }

}
