css:
@-moz-document domain("boards.4chan.org") {

html
{
    background-image:url('http://i.imgur.com/T3UKV.png') !important;
    background-color:#eeeeee !important;
    background-repeat: no-repeat !important;
    background-position: 800px 166px !important;
    background-attachment:fixed !important;
}

body, div.pagelist.desktop
{
    background: transparent !important;
    color: #000000 !important;
}

div.boardTitle, div.boardSubtitle, td, div#boardNavDesktop.desktop, div#boardNavDesktopFoot.desktop, div.postingMode.desktop, div#qrHeader.drag.postblock
{
    color: #000000 !important;
}

td
{
    background-color:transparent !important;
}

hr, td, div.pagelist.desktop, div#threadWatcher.extPanel.reply, li, .post.reply, div#threadWatcher
{
    border-color: transparent !important;
}

a
{
    color: #444444 !important;
}

a:hover
{
    color: #666666 !important;
}

.post.reply, .sideArrows, div#threadWatcher.extPanel.reply, div.postingMode.desktop, div#quickReply.extPanel.reply, div#qrHeader.drag.postblock, div#threadWatcher
{
    background-color:#dddddd !important;
}

.post.reply.highlight, .post.reply.preview, .post.op.preview
{
    background-color:#cccccc !important;
}

.sideArrows, span.quote, span.name, span.postertrip
{
    color: #6666ff !important;
}

.sideArrows
{
    border-color: #dddddd !important;
    border-style: solid !important;
    border-width: 0px 5px 0px 3px !important;
    border-radius: 5px 0px 0px 5px !important;
}

textarea:focus, input:focus, div#qrHeader.drag.postblock, div#quickReply.extPanel.reply
{
    border-color: #aaaaaa !important;
}

ul.rules, img.title, div#absbot.absBotText
{
    display: none !important;
}
}
