css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dolphin-emu.org") {
.trow1, .trow_sep, .post_author, #container, blockquote, textarea, code, .textbox, body, .alert, #content, .inline_row .trow1 td{
    background: #2C2C2C !important;
}
.trow2, .hero-unit, .post_content, .post_controls, .my-hero, .well, #spinner, #mce_editor_0_editor_tab a, #mce_editor_0_code_tab a, .containerHead, .plainlinks, #mw-head, .sidebar-nav, .infobox, .wikitable, .toc, .infobox tbody tr, .inline_row .trow2 td {
    background: #333 !important;
}
.pm_alert, .tborder, .spoiler, .spoiler_contenido, .new_reply_button, .postbit_edit, .postbit_qdelete, .postbit_quote, .postbit_email, .postbit_pm, .postbit_website, .postbit_multiquote,.postbit_multiquote_on, .postbit_report, .postbit_find, .spoiler_cabecera, .codeblock, .trow_sep, .pagination_current,  pre, .active a, .wikitable tbody tr th, .headerSort, .catlinks, .thumbinner, .collapsed.jquery-tablesorter tfoot tr th, .collapsed.jquery-tablesorter thead tr th {
    background: #111 !important;
    color: #eee !important;
}
.thead, .tcat {
    background: #000 !important;
}
.trow1, trow2 {
    border-bottom: 1px solid #111 !important;
    border-top: 1px solid #111 !important;
}
a {
    color: steelblue !important;
}
a, b, li, p, h1, h2, h3, h4, span, div {
    text-shadow: none !important;
}
span, .smalltext, .trow_sep, textarea, body, .textbox, div, h1, h2, h5, li, th, .version, .reldate, td, p {
    color: #eee !important;
}
a.btn {
    color: #111 !important;
}
}

@-moz-document domain("bugs.dolphin-emu.org") {
.issue, div.issues, .members, tr[class="even"] {
    background: #333 !important;
}
#main, tr[class="odd"] {
    background: #2C2C2C !important;
}
#footer, h3, div[class="autoscroll"] th, table[class="list issue-report"] th {
    background: #111 !important;
    color: #eee !important;
}
strong {
    color: #aaa !important;
}
}
