css:
/* about.com Search Box Mover */

/* This script moves the new search box to a top position
   and makes it pop up on mouse hover */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.about.com") {

div#ptcBrandFooter.ptcWrapperHeader {
background-image:url(http://z.about.com/i/d/spb.gif) !important;
background-repeat:no-repeat !important;
background-position:-328px 10px !important;
position:absolute !important;
top:135px !important;
max-height:1px !important;
overflow:auto;
}

div#ptcBrandFooter.ptcWrapperHeader:hover {
max-height:330px !important;
}

div#ptcBodyDiv {
position:relative !important;
top:70px !important;
}

}
