css:
@-moz-document domain("metafilter.com") {

div.posttitle a {
    border-bottom-width: 1px;
    border-style: dotted;
    border-top-width: 0;
    border-right-width: 0;
    border-color: rgba(200,200,200,0.5);
    font-weight: bold;
    font-size: 14px;
    width: 100%;
    border-left-width: 0;
    color:#cc0;
}

div.posttitle a:hover {
    color: #cc9;
    border-color: rgba(204,204,153,0.5);
}

div.posttitle a:visited {
    color: #cc9;
    border-color: rgba(204,204,153,0.5);
}

div.posttitle a:visited:hover {
    color: #ddd;
}



}
