css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://play.google.com/music){



#current-loading-progress {
    -webkit-animation: fadeIn 1s;
    -webkit-transition: all 1s linear 0.1s;
}
*:active {
    -webkit-animation: fadeIn 1s;
    -webkit-transition: all 1s linear 0.1s;
}
#oneGoogleWrapper { display: none }
.search-button,
.search .goog-flat-button-hover,
.search .goog-flat-button-active,
.search .goog-flat-button-disabled { display: none !important }
.search {
    position: absolute;
    right: 5px;
}
bodasdfy {
    position: absolute;
    margin-top: -100px;
}
#player {
    position: absolute;
    bottom: 0px;
}
#nav {
    background: #F4F4F4 !important;
    border-right: 1px solid #e5e5e5;
    margin-right: 0px !important;
}
#headerBar { margin-left: 220px !important }
#promo_pack_bar { display: none !important }
.fade-out-effect { display: none !important }
.menu-bar { display: none !important }
#searchBox {
    border-radius: 20px !important;
    text-align: left !important;
    width: 220px !important;
    font-family: 'Proxima Nova',arial,sans-serif;
    font-size: 13px !important;
    padding: 2px !important;
    padding-bottom: 3px !important;
    text-indent: 10px !important;
    margin-right: 10px;
}
.ac-renderer {
    margin-right: 10px !important;
    box-shadow: 0px 0px 15px #888888 !important;
    margin-left: -5px !important;
    margin-top: 5px !important;
    overflow: none !important;
}
.nav-item-container { border-radius: 5px }
#header {
    height: 35px !important;
    background: none !important;
}
.nav-header { display: none !important }
#syncing_indicator { display: none !important }
#coloredBar {
    background: none !important;
    border-top: 0px solid #e5e5e5 !important;
}
#breadcrumbs { padding-bottom: 11px }
#headerBar .tab-text a { border-bottom: 2px solid lightgrey }
#countSummary { border-bottom: 1px solid lightgrey }
iframe,
.artistViewRelatedInfoContainer { display: none }
.selectedSong { background-color: #ed8335 }
.nav-item-container.selected {
    border-bottom: 1px solid lightgray;
}

.nav-item-container{
    border-bottom: 1px solid #f4f4f4;
}

#nav-item-container{
    border-bottom: 1px solid #f4f4f4;
}

.modal-dialog-bg, .modal-dialog{
}
}
