css:
@-moz-document domain("developer.chrome.com") {
#gc-topnav
{
	overflow: auto !important;
	width: 100% !important;
}

#gc-sidebar
{
	background-color: white !important;
	border: 1px solid silver; border-radius: 1em !important;
	height: 3em !important;
	/* height: 90% !important; */ /* 4 screenshot */
	margin-left: 1em !important; margin-top: 0.8em !important;
	overflow: hidden !important;
	padding-right: 2.5em !important; padding-left: 1em !important;
	position: fixed !important; top: 0 !important; left: 0 !important;
	 
	z-index: 999999 !important; 
}

#gc-sidebar:hover
{
	height: 90% !important;
	overflow-y: scroll !important;
}


#gc-sidebar ul
{
	margin: 0px !important;
}

#gc-sidebar ul li:first-of-type
{
	border-top: 0px !important;
}

#gc-pagecontent
{
	margin-left: 0px !important;
}

.gsc-control-searchbox-only
{
	position: fixed !important;
	top: 1em !important;
	right: 1em !important;
}

#header
{
	z-index: 999998 !important;
}
}
