css:
@-moz-document domain("facebook.com") {
body {
opacity: .5;
background-color: #ddd;
color: #fff;
}

#pageLogo a { background-image: url(data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAIAOw==) !important; }
#blueBar, .titlebar, .titlebar:hover {
background-color: #000;
}

#mainContainer {
border-right: 0px;
}
#globalContainer, #content, #contentArea, #contentCol, #rightCol, .rightColumnWrapper {
background-color: #ddd;
color: #fff;
}
.linkWrap {
}
.fbChatConvItem, .conversation, .fbNubFlyoutBody {
color: #999 !important;
background-color: #ddd;
}
.fbNubFlyoutTitlebar {
background-color: white !important;
}
.fbMercuryChatTab .titlebar .titlebarText {
color: black !important;
}
._42fz a {
color: #aaa !important;
}
._42fz img.pic.img {
filter: alpha(opacity=50);
opacity: 0.5;
}
#rightCol {
display: auto;
}
#pagelet_ego_pane_w {
display: none;
}
#mainContainer {
border-right: 1px solid #000 !important;
min-height: 600px !important;
}
}

@-moz-document url-prefix("http://www.facebook.com/attachments/messaging_upload.php?") {
html, body, form {
-moz-appearance:none!important; -webkit-appearance:none!important; background-color:transparent!important;
}
}

@-moz-document url-prefix("http://www.facebook.com/plugins/") {
#facebook,html,body{ background:none!important }
}
