css:
/* name: mozilla-remix.seesaa.net */
/* last update 2013/03/03 */

@-moz-document url-prefix("http://mozilla-remix.seesaa.net/"){

/*== user.css for mozilla-remix.seesaa.net/article/ ==*/

/*- unwanted contents -*/
div#banner+br,
div#banner+br+center,
#comments>a,
#comments>br
 {display: none !important;}

/*- header and footer -*/
#banner, #footer {
 background-repeat: repeat !important; /* 2013/03/03 */
 height: auto !important;
 width: 100% !important;
 padding: 0 !important;
 }
#banner a, #banner a:link,#banner a:visited, .description 
 {color: white !important; text-shadow: 0px 0px 3px white;} /* 2013/03/03 */
#footer>table {width: 100% !important;}

/*- 3columns relayout to requid 2013/03/03 -*/
body.layout-three-column {width: inherit !important; min-width: 800px;}
#box {
 width: 100% !important;
 margin:0 !important; padding: 0 !important; border: none !important;
 }
#links-right-box {}
#content {width: 100% !important; min-width: 376px; margin: 0px -212px !important;}
#content>div {width: auto !important; margin: 0px 212px !important;}
#links-left-box {
 float: right !important;
 }

}
