css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('.*(waze\.com\/editor).*') {

  #editor-container #WazeMap {
    background-color: #454545 !important;
  }

  .navbar .navbar-inner {
    height: 40px !important; 
    padding-top: 0px!important;
  }

  .navbar h1 {
    margin: 0px 11px 0 10px !important;
  }
  
  .container {
    padding-top: 0px !important;
  }

  .row-fluid #sidebar {
    width: 21% !important;
  }

  .row-fluid .fluid-fixed {
    margin-left: 21% !important;
    margin-right: 0% !important;
  }
  
  #toolbar {
    height: 40px !important;
  }
  
  #edit-buttons {
    height: 40px !important;
  }
  
  #edit-buttons .toolbar-button.WazeControlDeleteFeature {
    height: 40px !important;
  }
  
  #edit-buttons .toolbar-button.WazeControlRedoControl {
    height: 40px !important;
  }

  #edit-buttons .toolbar-button.WazeControlUndoControl {
    height: 40px !important;
  }

  #edit-buttons .toolbar-button.WazeControlSave {
    height: 40px !important;
  }

  #edit-buttons .toolbar-button.WazeControlSave .counter {
    top: 9px !important;
  }

  #edit-buttons .toolbar-button.toolbar-group-drawing {
    height: 40px !important;
  }

  #map-search {
    height: 40px !important;
    line-height: 40px !important;
  }

  #map-search input[type="submit"] {
    height: 40px !important;
  }

  #map-search .input-wrapper {
    height: 40px !important;
  }

  #map-search .search-query {
    height: 10px !important;
  }
}
