css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.oreilly.com") {

  body {
     background-image: none !important;
  /*   background-color: white !important; */
  }

  #header, #nav, #subnav, #search-box, #leftcol, #sidebar, .asset-body > div, #comments, #footer, .ISI_IGNORE
    { display: none !important; }

  #home, #page, #wrapper, #container, .entrycontainer, .entry-asset
  {
     width: 100% !important;
     max-width: none !important;
     margin: 0 !important;
     padding: 0 !important;
     float: none !important;
  }

  .entry-content, .asset-body
  {
     padding: 2% !important;
     width: 96% !important;
     text-align: justify !important;
     font-size: 12pt !important;
     font-family: arial, sans-serif !important;
     max-width: none !important;
  /*
     position: absolute !important;
     left: 0 !important;
  */
  }


}
