css:
@-moz-document domain("deviantart.com") {
.comments, .favourites, .downloads, .faves, .comments-footer-container, .commentcount {
    display: none !important;
}

.commentslink {
    display: none !important;
}

#oh-menu-split {
    display: none !important;
}

.moods {
    display: none !important;
}

.details-section:nth-of-type(7) {
    display: none !important;
}

.details-section:nth-of-type(11) {
    display: none !important;
}

.ch-boxtop:nth-of-type(6) {
    display: none !important;
}

.ch-boxtop:nth-of-type(10) {
    display: none !important;
}

.mcb-commentcount {
    display: none !important;
}

}

@-moz-document url-prefix("http://my.deviantart.com/messages") {
.c {
    display: none !important;
}


}
