css:
@-moz-document domain("tumblr.com") {
#posts_control .count, li .followers .count {
    display: none !important;
}

*[id*='note_link_current'] {
    display: none !important;
}

*[id*='show_notes_link'] {
    display: none !important;
}

.notes, .notecount, .note-count, .note_count {
    display: none !important;
}

*[class*='notes'] {
    display: none !important;
}

#notes {
    display: none !important;
}

.title_and_controls > h1 {
    display: none !important;
}

.creditlinks {
    display: none !important;
}

.notification_like {
    display: none !important;
}
}
