@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {

body { background-color: #FDF6E3; color: #586E75; }
body > center > table { width: 640px !important; }
table {background:none !important; }
.title { 
 font-family: sans-serif; 
 font-size: 16px !important; }
}

table table tr {
  height: 18px !important;
}

.title a { color: #586e75 !important; }
.title a:visited { color: #839496 !important; }
.subtext { font-size: 10px !important; }
.comment font {font-size: 14px !important; color: #586E75 !important; line-height: 21px; }
