css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://correo.calat.com/") {

body {background: #111 !important; color: #777 !important; -moz-appearance: none !important;}
input {font-family: Calibri, Sans-Serif !important; background: #111 !important; color: #777 !important; border: 1px solid  #777 !important; padding: 1px 2px 1px 2px !important; -moz-appearance: none !important;}
input[type=submit]:active {background: #777 !important; color: #111 !important; -moz-appearance: none !important;}
a{color: #070 !important;}
a:hover,a:visited,a:active{color: #050 !important;}
#estado{border-bottom:1px solid #777 !important;}
}

@-moz-document url("http://www.calat.com/frase.html") {
hr{color:#777 !important;}
i{color:#777 !important;}

}
