css:
@-moz-document domain("roanoke.com") {
* {color: #B0AEAC !important;}
a {color: #A34848 !important;}
a:hover {color: #CF6161 !important;}
a:visited {color: #A86F6F !important;}
body, .breakingNewsBox {background: black !important; background-color: black !important;}
.mast-header, .update-box {background: #111111 !important;}
#pre-header {background-color: black !important;}
#page, #blog_pageContainer, #footerContainer {background: #1A1A1A !important;}
input {background-color: #1F1F1F !important;}
textarea, #respond {background-color: #1F1F1F !important;}
p.entry-footer {background: #1F1F1F !important;}
h1 {background-color: #111111 !important; color: #B0AEAC !important;}
h3, h4 {background: #1A1A1A !important;}
.style1 {background-color: #1F1F1F !important; border: 1px solid #111111 !important;}
cat a,#quicklinks,#xda_navbar td{background-color:#000808!important;border-color:#09c!important;border-left-color:#09c!important;border-right-color:#022!important;}
#footer {background-color: #111111 !important;}
#foot-news, #foot-ent, #foot-online, #foot-market {background-color: #1F1F1F !important;}
}
