@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.reocities.com") {

    #reocities_bar > DIV:nth-child(5) > FONT > CENTER:first-child > H2
    {display: none  !important;}

    #reocities_bar > DIV:nth-child(5) > FONT > CENTER:last-child > H2
    {display: none  !important;}

    #reocities_bar > H1 > A
    {display: none  !important;}

    #reocities_bar > H1
    {display: none  !important;}

    #banner_div
    {display: none  !important;}

    #nav > LI:first-child > FORM
    {display: none  !important;}

    #nav
    {display: none  !important;}

    div[id="reocities_bar"] {
    height: 0px !important;
    }
}
