css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pygame.org") {
body {
    background: none repeat scroll 0% 0% rgb(120, 120, 120) !important;
    }
div {
    color: rgb(255,255,255) !important;
    }
.h1title {
    font-size: 30px;
    background: none repeat scroll 0% 0% rgb(80, 80, 80) !important;
    color: rgb(210,200,200);
    }
h1 {
    background: none repeat scroll 0% 0% rgb(90, 90, 90) !important;
    color: rgb(210,200,200); 
    }
h2 {
    background: none repeat scroll 0% 0% rgb(90, 90, 90) !important; 
    color: rgb(210,200,200);
    }
h3 {
    background: none repeat scroll 0% 0% rgb(120, 120, 120) !important; 
    color: rgb(210,200,200);
    }
.newsdescription {
    background: none repeat scroll 0% 0% rgb(120, 120, 110) !important;
    }
.linky {
    background: none repeat scroll 0% 0% rgb(80, 150, 200) !important;
    }
.linky2 {
    background: none repeat scroll 0% 0% rgb(80, 170, 200) !important;
    }
.nav th{
    background: none repeat scroll 0% 0% rgb(100, 150, 256) !important;
    }
.nav td{
    background: none repeat scroll 0% 0% rgb(20, 200, 230) !important;
    }
}
