css:
@-moz-document domain('wikipedia.org') {
@media screen and (min-width: 1600px) {
 
	#firstHeading, .mw-headline {
		font-family: Georgia, Times New Roman, Times, serif;
	}
 
	#mw-content-text {
		margin-left:300px;
		margin-right:380px;
		font-size:11pt;
	}
 
	.tright {
		right:-380px;
		margin-left:-380px !important;
		position:relative;
		font-size:10pt;
	}
 
	.tright .thumbinner {
		width:342px !important;
	}
 
	.tleft .thumbinner {
		width:272px !important;
	}
 
	.tleft {
		left:-300px;
		margin-right:-300px !important;
		position:relative;
		font-size:10pt;
	}
 
	.infobox, .vertical-navbox, .mbox-small {
		right:-380px;
		margin-left:-380px !important;
		max-width:350px !important;
		width:350px !important;
		position:relative;
		font-size:10pt;
	}
 
	.toc {
		left:0px;
		position:absolute;
		float:none;
		top:80px;
		max-width:280px;
		width:280px;
		font-size:10pt;
	}
 
	.ambox {
		margin-left:0px !important;
		margin-right:0px !important;
		font-size:10pt;
	}
 
	.wikitable,.navbox {
		clear:none !important;
	}
 
}
}
