css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://freesound.org/tickets/moderation/assigned/"), url-prefix("http://www.freesound.org/tickets/moderation/assigned/") {


/* full width */
#container {
  width: auto !important;
}

/* make left and right side 49% wide, no margin or padding */
#moderation-queue-left, #moderation-queue-right {
  width: 49% !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* 1% of margin between the two for readability */
#moderation-queue-right {
  margin-left: 1% !important;
}

/* center all the text */
#user-annotations-section, #assigned-tickets-table-wrapper table, #moderation-queue-left h4 {
float: right;
}

/* make the list of sounds non-scrollable but fixed */
#assigned-tickets-table-wrapper {
  height: auto !important;
}

/* center header and top links */
h1, #ticket_menu {
  text-align: center;
}
#ticket_menu ul {
  display: inline-block;
  padding: 0;
}

}
