css:
*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var ) {
	font-family:"Caecilia LT Std Roman","CaeciliaLTStd-Bold","Microsoft Yahei" !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
pre,code,kbd,samp,var {
	font-family:"Monaco","Microsoft Yahei" !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
@-moz-document domain("douban.com") {
	*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
	font-family:"Caecilia LT Std Roman","CaeciliaLTStd-Bold","Microsoft Yahei",ilmenicons !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
}@-moz-document domain("tmall.com") {
	*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
	font-family:tm-detail-font,tmall3c,"Caecilia LT Std Roman","CaeciliaLTStd-Bold","Microsoft Yahei" !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
}@-moz-document domain("tower.im") {
	*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
	font-family:FontAwesome,"Caecilia LT Std Roman","CaeciliaLTStd-Bold","Microsoft Yahei" !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
}@-moz-document regexp(".*(maxiang.info|xiaoshujiang.com|zybuluo.com|chrome-extension|chrome-extension).*") {
	*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
	font-family:"Monaco",Arial,"Microsoft Yahei" !important;
	text-shadow:0.01em 0.01em 0.02em  #999999 !important;
}
}
