css:
h1.fullname {
  position:relative;
  display: inline;
}

h1.fullname:before {
  content:'';
  background-image:url(http://si0.twimg.com/a/1359591489/t1/img/twitter_web_sprite_icons.png);
  background-position: -120px -480px;
  width:21px;
  height:21px;
  display:block;
  position:absolute;
  right: -27px;
}

i.verified-large-border, span.verified-large-border , span.verified {
  display:none;
}
