css:
@-moz-document domain("tutorial.math.lamar.edu") {
    body {
        background-color: #222 !important;
        color: #ccc !important;
    }
    a:link,
    a:visited {
        color: #7070ff !important;
    }
    .Main_Heading {
        color: #ccc !important;
    }
    .Content_Main,
    .Content {
        background-color: #222 !important;
        color: #ccc !important;
    }
    .Content a {
        color: #eee !important;
    }
    .Content a:hover {
        color: #3589a7 !important;
    }
    .math1 {
        color: #ccc !important;
    }
    /*
    .MTDisplayEquation {
        background-color: #ddd !important;
        color: #222 !important;
        border-color: #d0d0d0 !important;
        border-width: 2pt !important;
        box-shadow: 0px 0px 10px #666 !important;
        border-radius: 2px;
        -moz-border-radius: 2px;
    }
    */
    .MPScreenEqn {
        background-color: #ddd !important;
        color: #222 !important;
        border-color: #d7d7d7 !important;
        border-width: 2pt !important;
        box-shadow: 0px 0px 5px #666 !important;
        border-radius: 2px;
        -moz-border-radius: 2px;
    }
    .MPPopup,
    .PopUp,
    .PopUp_Source_On,
    .PopUp_Source_Fixed_On,
    .PopUp_Source_Fixed_Off {
        background-color: #ddd !important;
        color: #222 !important;
        border-color: #d7d7d7 !important;
        border-width: 2pt !important;
        box-shadow: 0px 0px 40px #333 !important;
        border-radius: 3px;
        -moz-border-radius: 3px;
    }
    /* Theorems/Examples Background */
    .MsoNormalTable {
        background: #203C3C !important;
    }
    .PrevNextButtonOff {
        background-color: #444 !important;
        border-color: #555 !important;
        color: #eee !important;
    }
    .PrevNextButtonOn {
        background-color: #777 !important;
        border-color: #777 !important;
        color: #fff !important;
    }
}
